import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { selectCount } from "../userSlice";
import { selectLoggedInUser } from "../../auth/authSlice";

export default function ProfileUser() {
  const dispatch = useDispatch();
  const user = useSelector(selectLoggedInUser);

const editHandle = () => {}

  return (
    <div>
      <div className="mx-auto bg-white max-w-2xl px-4 sm:px-6 lg:px-8">
        <div className="border-t border-gray-200 px-4 py-6 sm:px-6">
          <h1 className="text-2xl font-bold tracking-tight text-gray-900">
            Name: {user.name ? user.name : "Guest"}
          </h1>
          <h2 className="text-1xl font-bold tracking-tight text-red-900">
            Email: {user.email}
          </h2>
        </div>

        <div className="border-t border-gray-200 px-4 py-6 sm:px-6">
          <p className="mt-0.5 text-sm text-gray-500">Shipping Address:</p>
          {user.addresses.map((address) => (
            <div className="flex justify-between gap-x-6 px-5 py-5 border-solid border-2 border-gray-200">
              <div className="flex min-w-0 gap-x-4">
                <div className="min-w-0 flex-auto">
                  <p className="text-sm font-semibold leading-6 text-gray-900">
                    Name: {address.name}
                  </p>
                  <p className="mt-1 truncate text-xs leading-5 text-gray-500">
                    Street: {address.street}
                  </p>
                  <p className="text-sm leading-6 text-gray-900">
                    City: {address.city}
                  </p>
                </div>
              </div>
              <div className="hidden shrink-0 sm:flex sm:flex-col sm:items-end">
                <p className="text-sm leading-6 text-gray-900">
                  Postal Code: {address.postalcode}
                </p>
                <p className="text-sm leading-6 text-gray-900">
                  Country: {address.country}
                </p>
                <p className="text-sm leading-6 text-gray-900">
                  Province: {address.state}
                </p>
                <p className="text-sm leading-6 text-gray-900">
                  Phone: {address.phone}
                </p>
              </div>
              <div className="hidden shrink-0 sm:flex sm:flex-col sm:items-end">
                <button
                  onClick={(e) => editHandle(e, item.id)}
                  type="button"
                  className="font-medium text-indigo-600 hover:text-indigo-500"
                >
                  Edit
                </button>

                <button
                  onClick={(e) => removeHandle(e, item.id)}
                  type="button"
                  className="font-medium text-indigo-600 hover:text-indigo-500"
                >
                  Remove
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
